    <style {csp-style-nonce}>
        .admin-stats {
            display: flex;
            align-items: center;
            justify-content: space-between;
            margin: 40px 0px;
            gap: 24px;
        }
        .teacher-stats,
        .student-stats,
        .class-stats {
            display: flex;
            flex-direction: column;
            width: 100%;
            border: 1px solid #C5C5C5;
            padding: 24px;
            border-radius: 24px;
            gap: 12px;
            cursor: pointer;
            transition: background-color 0.3s ease;
        }
        .teacher-stats:hover,
        .student-stats:hover,
        .class-stats:hover {
            border: 1px solid #303030;
            background-color: #303030;
            transition: background-color 0.3s ease;
        }
        .teacher-stats:hover span,
        .student-stats:hover span,
        .class-stats:hover span,
        .teacher-stats:hover path,
        .student-stats:hover path,
        .class-stats:hover path {
            color: #fff;
            fill: #fff;
        }
        .svg-wrap {
            display: flex;
            align-items: center;
            justify-content: space-between;
        }
        .total-count {
            font-size: 32px;
            font-weight: 600;
        }
    </style>
