<style {csp-style-nonce}>
    .main-navbar {
        width: auto;
        border-right: 1px solid #CECECE;
    }
    .navbar-wrap {
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        height: 100%;
        padding: 16px 35px;
    }
    .navbar-logo-wrapper,
    .navbar-user-details-wrapper,
    .navbar-contents-wrapper,
    .navbar-bottom {
        display: flex;
        flex-direction: column;
        align-items: center;
    }
    .navbar-logout-wrapper {
        display: flex;
        align-items: center;
        gap: 12px;
    }
    .navbar-top {
        display: flex;
        flex-direction: column;
        gap: 40px;
    }
    .initial-circle {
        background-color: #4495FA;
        border-radius: 50%;
        width: 60px;
        height: 60px;
        display: flex;
        align-items: center;
        justify-content: center;
        font-size: 32px;
        color: #FFFFFF;
        font-weight: 700;
    }
    .user-email {
        color: #838383;
        font-size: 14px;
    }
    .navbar-contents-wrapper {
        align-items: flex-start;
        gap: 28px;
        margin: 0px 26px;
    }
    .navbar-contents-wrapper span,
    .navbar-logout-wrapper span {
        font-size: 14px;
        color: #303030B2;
    }
    .navbar-content:hover svg {
        opacity: 1;
    }
    .navbar-content:hover path {
        fill: #303030;
    }
    #special-svg:hover path {
        fill: none;
        stroke: #303030;
    }
    .navbar-content:hover span {
        color: #303030;
        font-weight: 500;
    }
    .navbar-contents-wrapper .navbar-content {
        display: flex;
        align-items: center;
        gap: 12px;
        cursor: pointer;
    }
    .navbar-content svg,
    .navbar-bottom svg {
        opacity: 0.7;
    }
    .navbar-bottom {
        border: 1px solid #C5C5C5;
        padding: 6px;
        display: flex;
        align-items: center;
        justify-content: center;
        border-radius: 12px;
        width: 100%;
        cursor: pointer;
        transition: background-color 0.8s ease;
    }
    .navbar-bottom:hover {
        border: 1px solid #303030;
        background-color: #303030;
        transition: background-color 0.8s ease;
    }
    .navbar-bottom:hover svg {
        opacity: 1;
    }
    .navbar-bottom:hover path,
    .navbar-bottom:hover span {
        color: #FFFFFF;
        fill: #FFFFFF;
    }
</style>
